import React, { useState, useEffect } from 'react';
import API from '../api';

export default function Community({ username }) {
  const [posts, setPosts] = useState([]);
  const [text, setText] = useState('');
  const [image, setImage] = useState(null);
  const [preview, setPreview] = useState(null);
  const [commentText, setCommentText] = useState({});
  const [comments, setComments] = useState({});
  const [likes, setLikes] = useState({});
  const [liked, setLiked] = useState({});

  // 获取所有帖子
  const fetchPosts = async () => {
    const res = await API.getPosts();
    setPosts(res.data);
    // 获取每个帖子的评论和点赞
    res.data.forEach(post => {
      fetchComments(post.id);
      fetchLikes(post.id);
    });
  };

  useEffect(() => {
    fetchPosts();
    // eslint-disable-next-line
  }, []);

  // 处理图片选择
  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setImage(file);
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => setPreview(reader.result);
      reader.readAsDataURL(file);
    } else {
      setPreview(null);
    }
  };

  // 发布分享
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!text && !image) return;
    const formData = new FormData();
    formData.append('text', text);
    formData.append('username', username);
    if (image) formData.append('image', image);
    await API.createPost(formData);
    setText('');
    setImage(null);
    setPreview(null);
    fetchPosts();
  };

  // 评论相关
  const fetchComments = async (postId) => {
    const res = await API.getComments(postId);
    setComments(prev => ({ ...prev, [postId]: res.data }));
  };
  const handleComment = async (postId) => {
    if (!commentText[postId]) return;
    await API.createComment({ postId, text: commentText[postId], username });
    setCommentText(prev => ({ ...prev, [postId]: '' }));
    fetchComments(postId);
  };

  // 点赞相关
  const fetchLikes = async (postId) => {
    const res = await API.getLikes(postId);
    setLikes(prev => ({ ...prev, [postId]: res.data.count }));
    // 判断当前用户是否已点赞
    if (username) {
      const res2 = await API.getComments(postId); // 用评论接口判断点赞用户（如需优化可加新接口）
      setLiked(prev => ({ ...prev, [postId]: false })); // 简化：不判断，点一次切换
    }
  };
  const handleLike = async (postId) => {
    await API.toggleLike({ postId, username });
    fetchLikes(postId);
  };

  return (
    <div style={{ maxWidth: 600, margin: '40px auto', padding: 24, background: '#fff', borderRadius: 8, boxShadow: '0 2px 8px #eee' }}>
      <h2 style={{ textAlign: 'center' }}>社区分享</h2>
      <form onSubmit={handleSubmit} style={{ marginBottom: 32 }}>
        <textarea
          value={text}
          onChange={e => setText(e.target.value)}
          placeholder="分享你的想法..."
          rows={3}
          style={{ width: '100%', marginBottom: 12, padding: 8, borderRadius: 4, border: '1px solid #ccc' }}
        />
        <input type="file" accept="image/*" onChange={handleImageChange} style={{ marginBottom: 12 }} />
        {preview && <div style={{ marginBottom: 12 }}><img src={preview} alt="预览" style={{ maxWidth: 200, borderRadius: 8 }} /></div>}
        <button type="submit" style={{ padding: '8px 24px', borderRadius: 4, background: '#007bff', color: '#fff', border: 'none' }}>发布</button>
      </form>
      <div>
        {posts.length === 0 && <div style={{ color: '#888', textAlign: 'center' }}>暂无分享</div>}
        {posts.map(post => (
          <div key={post.id} style={{ borderBottom: '1px solid #eee', padding: '16px 0' }}>
            <div style={{ fontWeight: 'bold', marginBottom: 4 }}>{post.username} <span style={{ color: '#aaa', fontSize: 12 }}>{new Date(post.createdAt).toLocaleString()}</span></div>
            {post.text && <div style={{ marginBottom: 8 }}>{post.text}</div>}
            {post.image && <img src={`http://localhost:3001${post.image}`} alt="分享" style={{ maxWidth: 300, borderRadius: 8, marginBottom: 8 }} />}
            <div style={{ display: 'flex', alignItems: 'center', gap: 12, margin: '8px 0' }}>
              <button onClick={() => handleLike(post.id)} style={{ color: liked[post.id] ? 'red' : '#333' }}>👍 {likes[post.id] || 0}</button>
            </div>
            {/* 评论区 */}
            <div style={{ marginTop: 8 }}>
              <div style={{ fontSize: 13, color: '#555', marginBottom: 4 }}>评论：</div>
              {(comments[post.id] || []).map(c => (
                <div key={c.id} style={{ fontSize: 13, marginBottom: 2 }}><b>{c.username}：</b>{c.text}</div>
              ))}
              <div style={{ display: 'flex', gap: 8, marginTop: 4 }}>
                <input
                  value={commentText[post.id] || ''}
                  onChange={e => setCommentText(prev => ({ ...prev, [post.id]: e.target.value }))}
                  placeholder="写评论..."
                  style={{ flex: 1, borderRadius: 4, border: '1px solid #ccc', padding: 4 }}
                />
                <button onClick={() => handleComment(post.id)} style={{ borderRadius: 4, background: '#eee', border: 'none', padding: '4px 12px' }}>发送</button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
