import React, { useState, useEffect } from 'react';
import API from '../api';
import { ThemeContext } from '../ThemeContext';

export default function Community({ username }) {
  const [posts, setPosts] = useState([]);
  const [text, setText] = useState('');
  const [image, setImage] = useState(null);
  const [preview, setPreview] = useState(null);
  const [commentText, setCommentText] = useState({});
  const [comments, setComments] = useState({});
  const [likes, setLikes] = useState({});
  const [liked, setLiked] = useState({});
  const [bgImages, setBgImages] = useState([]);
  const [bgIndex, setBgIndex] = useState(0);
  const [bgFade, setBgFade] = useState(true);
  const [reporting, setReporting] = useState(null); // {type, id}
  const [reportReason, setReportReason] = useState('');
  const { theme } = React.useContext(ThemeContext);

  // Ëé∑ÂèñÊâÄÊúâÂ∏ñÂ≠ê
  const fetchPosts = async () => {
    const res = await API.getPosts();
    setPosts(res.data);
    // Ëé∑ÂèñÊØè‰∏™Â∏ñÂ≠êÁöÑËØÑËÆ∫ÂíåÁÇπËµû
    res.data.forEach(post => {
      fetchComments(post.id);
      fetchLikes(post.id);
    });
  };

  useEffect(() => {
    fetchPosts();
    // eslint-disable-next-line
  }, []);

  // Ëé∑ÂèñÊâÄÊúâÈ£éÊôØÂõæÁâá
  useEffect(() => {
    async function fetchAllImages() {
      const dests = await API.get('/destinations');
      let imgs = [];
      for (let d of dests.data) {
        const res = await API.get(`/destinations/${d.id}/scenery`);
        if (res.data && res.data.length > 0) {
          imgs = imgs.concat(res.data.map(s => `http://localhost:3001${s.image}`));
        }
      }
      setBgImages(imgs);
    }
    fetchAllImages();
  }, []);

  // ËΩÆÊí≠ËÉåÊôØ
  useEffect(() => {
    if (bgImages.length === 0) return;
    setBgFade(false);
    const timer = setInterval(() => {
      setBgFade(true);
      setTimeout(() => {
        setBgIndex(i => (i + 1) % bgImages.length);
        setBgFade(false);
      }, 400);
    }, 4000);
    return () => clearInterval(timer);
  }, [bgImages]);

  // Â§ÑÁêÜÂõæÁâáÈÄâÊã©
  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setImage(file);
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => setPreview(reader.result);
      reader.readAsDataURL(file);
    } else {
      setPreview(null);
    }
  };

  // ÂèëÂ∏ÉÂàÜ‰∫´
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!text && !image) return;
    const formData = new FormData();
    formData.append('text', text);
    formData.append('username', username);
    if (image) formData.append('image', image);
    await API.createPost(formData);
    setText('');
    setImage(null);
    setPreview(null);
    fetchPosts();
  };

  // ËØÑËÆ∫Áõ∏ÂÖ≥
  const fetchComments = async (postId) => {
    const res = await API.getComments(postId);
    setComments(prev => ({ ...prev, [postId]: res.data }));
  };
  const handleComment = async (postId) => {
    if (!commentText[postId]) return;
    await API.createComment({ postId, text: commentText[postId], username });
    setCommentText(prev => ({ ...prev, [postId]: '' }));
    fetchComments(postId);
  };

  // ÁÇπËµûÁõ∏ÂÖ≥
  const fetchLikes = async (postId) => {
    const res = await API.getLikes(postId);
    setLikes(prev => ({ ...prev, [postId]: res.data.count }));
    // Âà§Êñ≠ÂΩìÂâçÁî®Êà∑ÊòØÂê¶Â∑≤ÁÇπËµû
    if (username) {
      const res2 = await API.getComments(postId); // Áî®ËØÑËÆ∫Êé•Âè£Âà§Êñ≠ÁÇπËµûÁî®Êà∑ÔºàÂ¶ÇÈúÄ‰ºòÂåñÂèØÂä†Êñ∞Êé•Âè£Ôºâ
      setLiked(prev => ({ ...prev, [postId]: false })); // ÁÆÄÂåñÔºö‰∏çÂà§Êñ≠ÔºåÁÇπ‰∏ÄÊ¨°ÂàáÊç¢
    }
  };
  const handleLike = async (postId) => {
    await API.toggleLike({ postId, username });
    fetchLikes(postId);
  };

  // Âà†Èô§Â∏ñÂ≠ê
  const handleDelete = async (postId) => {
    if (!window.confirm('Á°ÆÂÆöË¶ÅÂà†Èô§ËøôÊù°Âä®ÊÄÅÂêóÔºü')) return;
    await API.deletePost(postId, username);
    fetchPosts();
  };

  const handleReport = async () => {
    if (!reportReason) return alert('ËØ∑Â°´ÂÜô‰∏æÊä•ÂéüÂõ†');
    await API.reportContent({ type: reporting.type, targetId: reporting.id, reason: reportReason, username });
    setReporting(null);
    setReportReason('');
    alert('‰∏æÊä•Â∑≤Êèê‰∫§');
  };

  const handleDeleteComment = async (commentId) => {
    if (!window.confirm('Á°ÆÂÆöË¶ÅÂà†Èô§ËøôÊù°ËØÑËÆ∫ÂêóÔºü')) return;
    await API.deleteComment(commentId, username);
    fetchComments(reporting.id); // ÈáçÊñ∞Ëé∑ÂèñËØÑËÆ∫ÂàóË°®
    setReporting(null);
  };

  return (
    <div style={{ minHeight: '100vh', backgroundImage: bgImages.length ? `url(${bgImages[bgIndex]})` : '', backgroundSize: 'cover', backgroundPosition: 'center', position: 'relative', transition: 'background-image 0.5s' }}>
      <div style={{ position: 'absolute', top:0, left:0, width:'100%', height:'100%', background: theme==='dark'?'rgba(0,0,0,0.7)':'rgba(0,0,0,0.4)', zIndex:0, transition: 'opacity 0.4s' }} />
      <div style={{ position:'relative', zIndex:1, maxWidth: 600, width: '96%', margin: '0 auto', padding: '4vw 2vw', background: theme==='dark'?'rgba(30,30,30,0.7)':'rgba(255,255,255,0.7)', borderRadius: 12, boxShadow: '0 2px 8px #eee', marginTop: '6vw', backdropFilter: 'blur(2px)', color: theme==='dark'?'#eee':'#222', transition:'background 0.4s,color 0.4s' }}>
        <h2 style={{ textAlign: 'center', fontSize: '6vw', marginBottom: '4vw' }}>Á§æÂå∫ÂàÜ‰∫´</h2>
        <form onSubmit={handleSubmit} style={{ marginBottom: 32 }}>
          <textarea
            value={text}
            onChange={e => setText(e.target.value)}
            placeholder="ÂàÜ‰∫´‰Ω†ÁöÑÊÉ≥Ê≥ï..."
            rows={3}
            style={{ width: '100%', maxWidth: '100%', minWidth: 0, boxSizing: 'border-box', marginBottom: 12, padding: 12, borderRadius: 6, border: '1.5px solid #bbb', fontSize: '4vw', minHeight: 60, background: '#fafbfc', outline: 'none' }}
          />
          <input type="file" accept="image/*" onChange={handleImageChange} style={{ marginBottom: 12 }} />
          {preview && <div style={{ marginBottom: 12 }}><img src={preview} alt="È¢ÑËßà" style={{ maxWidth: '80vw', borderRadius: 8 }} loading="lazy" /></div>}
          <button type="submit" style={{ padding: '10px 0', width: '100%', borderRadius: 6, background: '#007bff', color: '#fff', border: 'none', fontSize: '4vw', marginBottom: 8 }}>ÂèëÂ∏É</button>
        </form>
        <div>
          {posts.length === 0 && <div style={{ color: '#888', textAlign: 'center' }}>ÊöÇÊó†ÂàÜ‰∫´</div>}
          {posts.map(post => (
            <div key={post.id} style={{ borderBottom: '1px solid #eee', padding: '16px 0' }}>
              <div style={{ fontWeight: 'bold', marginBottom: 4, fontSize: '4vw' }}>
                {post.username} <span style={{ color: '#aaa', fontSize: '3vw' }}>{new Date(post.createdAt).toLocaleString()}</span>
                {post.username === username && (
                  <button onClick={() => handleDelete(post.id)} style={{ marginLeft: 12, color: 'red', background: 'none', border: 'none', cursor: 'pointer', fontSize: '3.5vw' }}>Âà†Èô§</button>
                )}
                <button onClick={() => setReporting({type:'post',id:post.id})} style={{ marginLeft: 12, color: '#ff9800', background: 'none', border: 'none', cursor: 'pointer', fontSize: '3.5vw' }}>‰∏æÊä•</button>
              </div>
              {post.text && <div style={{ marginBottom: 8, fontSize: '4vw' }}>{post.text}</div>}
              {post.image && <img src={`http://localhost:3001${post.image}`} alt="ÂàÜ‰∫´" style={{ maxWidth: '90vw', borderRadius: 8, marginBottom: 8 }} loading="lazy" />}
              <div style={{ display: 'flex', alignItems: 'center', gap: 12, margin: '8px 0' }}>
                <button onClick={() => handleLike(post.id)} style={{ color: liked[post.id] ? 'red' : '#333', fontSize: '5vw', background: 'none', border: 'none', transition: 'transform 0.2s', transform: liked[post.id] ? 'scale(1.2)' : 'scale(1)' }}>üëç {likes[post.id] || 0}</button>
              </div>
              {/* ËØÑËÆ∫Âå∫ */}
              <div style={{ marginTop: 8 }}>
                <div style={{ fontSize: '3.5vw', color: '#555', marginBottom: 4 }}>ËØÑËÆ∫Ôºö</div>
                {(comments[post.id] || []).map(c => (
                  <div key={c.id} style={{ fontSize: '3.5vw', marginBottom: 2, display:'flex', alignItems:'center' }}>
                    <b>{c.username}Ôºö</b>{c.text}
                    {c.username === username && (
                      <button onClick={() => handleDeleteComment(c.id)} style={{ marginLeft: 8, color: 'red', background: 'none', border: 'none', cursor: 'pointer', fontSize: '3vw' }}>Âà†Èô§</button>
                    )}
                    <button onClick={() => setReporting({type:'comment',id:c.id})} style={{ marginLeft: 8, color: '#ff9800', background: 'none', border: 'none', cursor: 'pointer', fontSize: '3vw' }}>‰∏æÊä•</button>
                  </div>
                ))}
                <div style={{ display: 'flex', gap: 8, marginTop: 4 }}>
                  <input
                    value={commentText[post.id] || ''}
                    onChange={e => setCommentText(prev => ({ ...prev, [post.id]: e.target.value }))}
                    placeholder="ÂÜôËØÑËÆ∫..."
                    style={{ flex: 1, borderRadius: 4, border: '1px solid #ccc', padding: 8, fontSize: '4vw' }}
                  />
                  <button onClick={() => handleComment(post.id)} style={{ borderRadius: 4, background: '#eee', border: 'none', padding: '4px 12px', fontSize: '4vw' }}>ÂèëÈÄÅ</button>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
      {/* ‰∏æÊä•ÂºπÁ™ó */}
      {reporting && (
        <div style={{ position:'fixed', left:0, top:0, width:'100vw', height:'100vh', background:'rgba(0,0,0,0.4)', zIndex:1000, display:'flex', alignItems:'center', justifyContent:'center' }}>
          <div style={{ background:'#fff', borderRadius:8, padding:24, minWidth:260 }}>
            <h3>‰∏æÊä•{reporting.type==='post'?'Âä®ÊÄÅ':'ËØÑËÆ∫'}</h3>
            <textarea value={reportReason} onChange={e=>setReportReason(e.target.value)} placeholder="ËØ∑Â°´ÂÜô‰∏æÊä•ÂéüÂõ†" style={{width:'100%',minHeight:60,margin:'12px 0',padding:8}} />
            <div style={{display:'flex',justifyContent:'flex-end',gap:12}}>
              <button onClick={()=>setReporting(null)}>ÂèñÊ∂à</button>
              <button onClick={handleReport} style={{background:'#ff9800',color:'#fff',border:'none',borderRadius:4,padding:'4px 16px'}}>Êèê‰∫§‰∏æÊä•</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
