import React, { useEffect, useState } from 'react';
import API from '../api';

export default function SceneryList({ destinationId }) {
  const [scenery, setScenery] = useState([]);

  useEffect(() => {
    if (destinationId) {
      API.get(`/destinations/${destinationId}/scenery`).then(res => setScenery(res.data));
    } else {
      setScenery([]);
    }
  }, [destinationId]);

  if (!destinationId) return null;

  return (
    <div>
      <h3>推荐风景</h3>
      {scenery.length === 0 && <div>暂无推荐</div>}
      <div style={{ display: 'flex', gap: 16 }}>
        {scenery.map(s => (
          <div key={s.id} style={{ border: '1px solid #ccc', padding: 8 }}>
            <div><b>{s.name}</b></div>
            <div>{s.description}</div>
            {s.image && <img src={`http://localhost:3001${s.image}`} alt={s.name} style={{ maxWidth: 120 }} />}
          </div>
        ))}
      </div>
    </div>
  );
}
