import React, { useState, useEffect } from 'react';
import API from '../api';

export default function Community({ username }) {
  const [posts, setPosts] = useState([]);
  const [text, setText] = useState('');
  const [image, setImage] = useState(null);
  const [preview, setPreview] = useState(null);
  const [commentText, setCommentText] = useState({});
  const [comments, setComments] = useState({});
  const [likes, setLikes] = useState({});
  const [liked, setLiked] = useState({});
  const [bgImages, setBgImages] = useState([]);
  const [bgIndex, setBgIndex] = useState(0);

  // Ëé∑ÂèñÊâÄÊúâÂ∏ñÂ≠ê
  const fetchPosts = async () => {
    const res = await API.getPosts();
    setPosts(res.data);
    // Ëé∑ÂèñÊØè‰∏™Â∏ñÂ≠êÁöÑËØÑËÆ∫ÂíåÁÇπËµû
    res.data.forEach(post => {
      fetchComments(post.id);
      fetchLikes(post.id);
    });
  };

  useEffect(() => {
    fetchPosts();
    // eslint-disable-next-line
  }, []);

  // Ëé∑ÂèñÊâÄÊúâÈ£éÊôØÂõæÁâá
  useEffect(() => {
    async function fetchAllImages() {
      const dests = await API.get('/destinations');
      let imgs = [];
      for (let d of dests.data) {
        const res = await API.get(`/destinations/${d.id}/scenery`);
        if (res.data && res.data.length > 0) {
          imgs = imgs.concat(res.data.map(s => `http://localhost:3001${s.image}`));
        }
      }
      setBgImages(imgs);
    }
    fetchAllImages();
  }, []);

  // ËΩÆÊí≠ËÉåÊôØ
  useEffect(() => {
    if (bgImages.length === 0) return;
    const timer = setInterval(() => {
      setBgIndex(i => (i + 1) % bgImages.length);
    }, 4000);
    return () => clearInterval(timer);
  }, [bgImages]);

  // Â§ÑÁêÜÂõæÁâáÈÄâÊã©
  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setImage(file);
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => setPreview(reader.result);
      reader.readAsDataURL(file);
    } else {
      setPreview(null);
    }
  };

  // ÂèëÂ∏ÉÂàÜ‰∫´
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!text && !image) return;
    const formData = new FormData();
    formData.append('text', text);
    formData.append('username', username);
    if (image) formData.append('image', image);
    await API.createPost(formData);
    setText('');
    setImage(null);
    setPreview(null);
    fetchPosts();
  };

  // ËØÑËÆ∫Áõ∏ÂÖ≥
  const fetchComments = async (postId) => {
    const res = await API.getComments(postId);
    setComments(prev => ({ ...prev, [postId]: res.data }));
  };
  const handleComment = async (postId) => {
    if (!commentText[postId]) return;
    await API.createComment({ postId, text: commentText[postId], username });
    setCommentText(prev => ({ ...prev, [postId]: '' }));
    fetchComments(postId);
  };

  // ÁÇπËµûÁõ∏ÂÖ≥
  const fetchLikes = async (postId) => {
    const res = await API.getLikes(postId);
    setLikes(prev => ({ ...prev, [postId]: res.data.count }));
    // Âà§Êñ≠ÂΩìÂâçÁî®Êà∑ÊòØÂê¶Â∑≤ÁÇπËµû
    if (username) {
      const res2 = await API.getComments(postId); // Áî®ËØÑËÆ∫Êé•Âè£Âà§Êñ≠ÁÇπËµûÁî®Êà∑ÔºàÂ¶ÇÈúÄ‰ºòÂåñÂèØÂä†Êñ∞Êé•Âè£Ôºâ
      setLiked(prev => ({ ...prev, [postId]: false })); // ÁÆÄÂåñÔºö‰∏çÂà§Êñ≠ÔºåÁÇπ‰∏ÄÊ¨°ÂàáÊç¢
    }
  };
  const handleLike = async (postId) => {
    await API.toggleLike({ postId, username });
    fetchLikes(postId);
  };

  // Âà†Èô§Â∏ñÂ≠ê
  const handleDelete = async (postId) => {
    if (!window.confirm('Á°ÆÂÆöË¶ÅÂà†Èô§ËøôÊù°Âä®ÊÄÅÂêóÔºü')) return;
    await API.deletePost(postId, username);
    fetchPosts();
  };

  return (
    <div style={{ minHeight: '100vh', backgroundImage: bgImages.length ? `url(${bgImages[bgIndex]})` : '', backgroundSize: 'cover', backgroundPosition: 'center', position: 'relative' }}>
      <div style={{ position: 'absolute', top:0, left:0, width:'100%', height:'100%', background:'rgba(0,0,0,0.4)', zIndex:0 }} />
      <div style={{ position:'relative', zIndex:1 }}>
        <h2 style={{ textAlign: 'center' }}>Á§æÂå∫ÂàÜ‰∫´</h2>
        <form onSubmit={handleSubmit} style={{ marginBottom: 32 }}>
          <textarea
            value={text}
            onChange={e => setText(e.target.value)}
            placeholder="ÂàÜ‰∫´‰Ω†ÁöÑÊÉ≥Ê≥ï..."
            rows={3}
            style={{ width: '100%', marginBottom: 12, padding: 8, borderRadius: 4, border: '1px solid #ccc' }}
          />
          <input type="file" accept="image/*" onChange={handleImageChange} style={{ marginBottom: 12 }} />
          {preview && <div style={{ marginBottom: 12 }}><img src={preview} alt="È¢ÑËßà" style={{ maxWidth: 200, borderRadius: 8 }} /></div>}
          <button type="submit" style={{ padding: '8px 24px', borderRadius: 4, background: '#007bff', color: '#fff', border: 'none' }}>ÂèëÂ∏É</button>
        </form>
        <div>
          {posts.length === 0 && <div style={{ color: '#888', textAlign: 'center' }}>ÊöÇÊó†ÂàÜ‰∫´</div>}
          {posts.map(post => (
            <div key={post.id} style={{ borderBottom: '1px solid #eee', padding: '16px 0' }}>
              <div style={{ fontWeight: 'bold', marginBottom: 4 }}>
                {post.username} <span style={{ color: '#aaa', fontSize: 12 }}>{new Date(post.createdAt).toLocaleString()}</span>
                {post.username === username && (
                  <button onClick={() => handleDelete(post.id)} style={{ marginLeft: 12, color: 'red', background: 'none', border: 'none', cursor: 'pointer' }}>Âà†Èô§</button>
                )}
              </div>
              {post.text && <div style={{ marginBottom: 8 }}>{post.text}</div>}
              {post.image && <img src={`http://localhost:3001${post.image}`} alt="ÂàÜ‰∫´" style={{ maxWidth: 300, borderRadius: 8, marginBottom: 8 }} />}
              <div style={{ display: 'flex', alignItems: 'center', gap: 12, margin: '8px 0' }}>
                <button onClick={() => handleLike(post.id)} style={{ color: liked[post.id] ? 'red' : '#333' }}>üëç {likes[post.id] || 0}</button>
              </div>
              {/* ËØÑËÆ∫Âå∫ */}
              <div style={{ marginTop: 8 }}>
                <div style={{ fontSize: 13, color: '#555', marginBottom: 4 }}>ËØÑËÆ∫Ôºö</div>
                {(comments[post.id] || []).map(c => (
                  <div key={c.id} style={{ fontSize: 13, marginBottom: 2 }}><b>{c.username}Ôºö</b>{c.text}</div>
                ))}
                <div style={{ display: 'flex', gap: 8, marginTop: 4 }}>
                  <input
                    value={commentText[post.id] || ''}
                    onChange={e => setCommentText(prev => ({ ...prev, [post.id]: e.target.value }))}
                    placeholder="ÂÜôËØÑËÆ∫..."
                    style={{ flex: 1, borderRadius: 4, border: '1px solid #ccc', padding: 4 }}
                  />
                  <button onClick={() => handleComment(post.id)} style={{ borderRadius: 4, background: '#eee', border: 'none', padding: '4px 12px' }}>ÂèëÈÄÅ</button>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
